import * as React from "react"
import {ListItemText} from "@material-ui/core"
import DeleteIcon from '@material-ui/icons/Delete'
import List from "@material-ui/core/List"
import ListItem from "@material-ui/core/ListItem"
import {ITodo} from "../types/type.todo"
import {deleteTodoById, getTodos} from "../api/requests"
import ListItemSecondaryAction from "@material-ui/core/ListItemSecondaryAction"
import IconButton from "@material-ui/core/IconButton"
import {useEffect} from "react"
import DialogContent from "@material-ui/core/DialogContent"
import DialogTitle from "@material-ui/core/DialogTitle"
import Dialog from "@material-ui/core/Dialog"
import {useConfirm} from "material-ui-confirm"

export default function ShowTodos () {

  const [todos, setTodos] = React.useState<ITodo[]>([]);

  const confirm = useConfirm();



  useEffect(() => {

      getTodos()
      .then(({data: {todos}}: ITodo[] | any) => {
        const todoList = todos
       setTodos(todoList)
      })
      .catch((err: Error) => console.log(err))


  })

  const deleteTodo = (id: string) => {

    deleteTodoById(id).then(({data: {todos}}: ITodo[] | any) => {
      const stuff = todos
      setTodos(stuff)
    })
    .catch((err: Error) => console.log(err))

  }

  const handleConfirmation = (id: string) => {

    confirm({ description: 'This action is permanent!' })
    .then(() => { deleteTodoById(id)})
    .catch(() => { /* ... */ });
  }




  return (
      <List>
        {todos.map((todo: ITodo ) => (
              <ListItem>
                <ListItemText key={todo._id}
                    primary={todo.text}
                />
                  <IconButton edge="end" aria-label="delete">

                    <DeleteIcon onClick={() => handleConfirmation(todo._id)}/>
                  </IconButton>
              </ListItem>
            )
        )}
      </List>
  );

}
