import FormControl from "@material-ui/core/FormControl"

import * as React from "react"
import {Button, OutlinedInput} from "@material-ui/core"
import InputLabel from "@material-ui/core/InputLabel"
import Paper from "@material-ui/core/Paper"
import  { handleSaveTodo } from "../api"
import {ITodo} from "../types/type.todo"
import {makeStyles} from "@material-ui/core/styles"

export default function AddTodo () {

  const useStyles = makeStyles((theme) => ({
    root: {
      display: 'flex',
      flexWrap: 'wrap',
      '& > *': {
        margin: theme.spacing(1),
        width: theme.spacing(14),
        height: theme.spacing(14),
      },
    },
  }));

  const classes = useStyles();

  const [todo, setTodoValue] = React.useState<string>("something");


  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const value = event.target.value;
    setTodoValue(value);
  };


  const submitEvent = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault()

    const todo1: Omit<ITodo, '_id'> = {
      name: "name",
      text: todo,
      status: false,
      createdAt: new Date().toDateString(),
    }
    handleSaveTodo(event, todo1)

  };


    return (
        <div>
        <Paper className={classes.root}>
          <form onSubmit={submitEvent} style={{
            display: 'flex',
            alignItems: 'center'
          }}>
            <OutlinedInput id="component-outlined" value={todo} onChange={handleChange} label="ToDo" />
            <Button size="small" type="submit">
              Add
            </Button>
          </form>
        </Paper>
        </div>
    );

}
