import * as React from "react"
import {useCallback, useEffect} from "react"
import {ITodo} from "../types/type.todo"
import {getTodos} from "../api/requests"
import {AddTodo} from "./AddTodo"
import {TableHead} from "@material-ui/core"
import TableRow from "@material-ui/core/TableRow"
import TableCell from "@material-ui/core/TableCell"
import TableSortLabel from "@material-ui/core/TableSortLabel"
import MaterialTable from "material-table"

export default function ShowTodos () {

  const [todos, setTodos] = React.useState<ITodo[]>([]);

  const headers = [
    { title: 'name', field: 'Name'},
    { title: 'description', field: 'Description'},
    { title: 'createdAt', field: 'Created At'}
  ]

  const handleStateChange = useCallback( id =>
      setTodos(todos.filter(todo => todo._id !== id)), [todos]
  )

  const updateTodos = useCallback( todo =>
      setTodos(todos.concat(todo)), [todos]
  )


  useEffect(() => {

      getTodos()
      .then(({data: {todos}}: ITodo[] | any) => {
        const todoList = todos
       setTodos(todoList)
      })
      .catch((err: Error) => console.log(err))
  }, [])

  console.log(todos)
        return (
      <MaterialTable columns={headers} data={todos}/>

  );

}
