import * as React from "react"
import {useCallback, useEffect} from "react"
import {ITodo} from "../types/type.todo"
import {getTodos} from "../api/requests"
import MaterialTable from "material-table"
import {SelectedRow} from "./SelectedRow"
import Dialog from "@material-ui/core/Dialog"
import DialogTitle from "@material-ui/core/DialogTitle"
import DialogContent from "@material-ui/core/DialogContent"
import {TextField} from "@material-ui/core"
import DialogActions from "@material-ui/core/DialogActions"
import Button from "@material-ui/core/Button"

export default function ShowTodos () {

  const [todos, setTodos] = React.useState<ITodo[]>([]);

  const [dialogState, setDialogState] = React.useState<boolean>(false);

  const headers = [
    { title: 'name', field: 'name'},
    { title: 'description', field: 'text'},
    { title: 'created at', field: 'createdAt'}
  ]

  const handleStateChange = useCallback( id =>
      setTodos(todos.filter(todo => todo._id !== id)), [todos]
  )

  const updateTodos = useCallback( todo =>
      setTodos(todos.concat(todo)), [todos]
  )


  useEffect(() => {

      getTodos()
      .then(({data: {todos}}: ITodo[] | any) => {
        const todoList = todos
       setTodos(todoList)
      })
      .catch((err: Error) => console.log(err))
  }, [])

        return (
            <>
      <MaterialTable title="To Dos" columns={headers} data={todos}
                     onRowClick={(event, selectedRow) => {
                       setDialogState(true)
                     }}/>

  <Dialog
      open={dialogState}
      aria-labelledby="form-dialog-title"
  >
    <DialogTitle id="form-dialog-title">Update</DialogTitle>
    <DialogContent>
      <TextField
          autoFocus
          margin="dense"
          id="name"
          fullWidth
      />
    </DialogContent>
    <DialogActions>

    </DialogActions>
  </Dialog>
                       </>
  );

}
