import axios, {AxiosResponse} from 'axios'
import {ITodo} from "../types/type.todo"

const api = axios.create({
  baseURL: 'http://localhost:4000',
});

const getTodos = async (): Promise<AxiosResponse<ITodo[]>> => {
  try {
    const todos: AxiosResponse<ITodo[]> = await axios.get(
        api + '/todos'
    )
    return todos
  } catch (error) {
    throw new Error(error)
  }
}

const addTodo = async (
    formData: ITodo
): Promise<AxiosResponse<string>> => {
  try {
    const todo: Omit<ITodo, '_id'> = {
      name: formData.name,
      text: formData.text,
      status: false,
      createdAt: formData.createdAt
    }

    const saveTodo: AxiosResponse<string> = await axios.post(
        api + '/add-todo',
        todo
    )
    return saveTodo
  } catch (error) {
    console.error(error)
    throw new Error(error)
  }
}



export {
  addTodo,
  getTodos
}
