import FormControl from "@material-ui/core/FormControl"

import * as React from "react"
import {Button, OutlinedInput} from "@material-ui/core"
import InputLabel from "@material-ui/core/InputLabel"
import Paper from "@material-ui/core/Paper"
import {handleSaveTodo} from "../api"

export default function AddTodo () {

  const [todo, setTodoValue] = React.useState<string>(null);


  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const value = event.target.value;
    setTodoValue(value);
  };


  const onSubmit = (event: React.FormEvent<HTMLFormElement>) => {

    handleSaveTodo(event, this.state.todo)

  };


    return (
        <Paper>
          <FormControl variant="outlined">
            <InputLabel htmlFor="component-outlined">Add</InputLabel>
            <OutlinedInput id="component-outlined" value={todo} onChange={handleChange} label="ToDo" />
            <Button size="small" type="submit">
              Add
            </Button>
          </FormControl>
        </Paper>
    );

}
