import * as React from "react"
import {useCallback, useEffect} from "react"
import {ITodo} from "../types/type.todo"
import {getTodos, updateTodo} from "../api/requests"
import {TextField} from "@material-ui/core"
import MaterialTable from "material-table"
import Dialog from "@material-ui/core/Dialog"
import DialogTitle from "@material-ui/core/DialogTitle"
import DialogContent from "@material-ui/core/DialogContent"
import DialogActions from "@material-ui/core/DialogActions"
import DeleteIcon from '@material-ui/icons/Delete';
import Button from "@material-ui/core/Button"
import {useConfirm} from "material-ui-confirm"

export default function ShowTodos () {

  const [todos, setTodos] = React.useState<ITodo[]>([]);

  const [todo, setTodo] = React.useState<ITodo>({
    _id: "",
    name: "",
    text: "",
    status: false,
    createdAt: ""
  });

  const [dialogState, setDialogState] = React.useState<boolean>(false);

  const confirmClick = useConfirm();

  const headers = [
    { title: 'name', field: 'name'},
    { title: 'description', field: 'text'},
    { title: 'created at', field: 'createdAt'}
  ]


  const tableIcons = {

  }

  const handleStateChange = useCallback( id =>
      setTodos(todos.filter(todo => todo._id !== id)), [todos]
  )

  const updateTodos = useCallback( todo =>
      setTodos(todos.concat(todo)), [todos]
  )


  useEffect(() => {

      getTodos()
      .then(({data: {todos}}: ITodo[] | any) => {
        const todoList = todos
       setTodos(todoList)
      })
      .catch((err: Error) => console.log(err))
  }, [])

  const handleUpdate = (todo: ITodo) => {

    confirmClick({ description: 'This action is permanent!' })
    .then(() =>  updateTodo(todo))
    .then(() => updateTodos)
    .catch((err: Error) => console.log(err))

  }
        return (
            <>
      <MaterialTable title="To Dos" columns={headers} data={todos}
                     options={{
                       pageSize:20
                     }}
                     onRowClick={(event, rowData) => {

                      setDialogState(true);
                     setTodo({_id: rowData._id,
                       name: rowData.name,
                       text: rowData.text,
                       status: rowData.status,
                       createdAt: rowData.createdAt
                     })
                     }}
                     actions={[
                       {
                         icon: 'delete',
                         tooltip: 'Delete Item',
                         onClick: (event, rowData) => window.confirm("You want to delete ")
                       }
                     ]}
      />

  <Dialog
      open={dialogState}
      onClose={() => setDialogState(false)}
      aria-labelledby="form-dialog-title"
  >
    <DialogTitle id="form-dialog-title">Update</DialogTitle>
    <DialogContent>
      <TextField
          defaultValue={todo.text}
          autoFocus
          margin="dense"
          id="name"
          fullWidth
      />
    </DialogContent>
    <DialogActions>
      <Button color="primary" onClick={() => setDialogState(false)}>
        Cancel
      </Button>
      <Button color="primary" onClick={() => {
        handleUpdate(todo)
        setDialogState(false)
      }}>
        Update
      </Button>
    </DialogActions>
  </Dialog>
</>
  );

}
