import * as React from "react"
import {ListItemText, TextField} from "@material-ui/core"
import DeleteIcon from '@material-ui/icons/Delete'
import List from "@material-ui/core/List"
import ListItem from "@material-ui/core/ListItem"
import {ITodo} from "../types/type.todo"
import {deleteTodoById, getTodos, updateTodo} from "../api/requests"
import ListItemSecondaryAction from "@material-ui/core/ListItemSecondaryAction"
import IconButton from "@material-ui/core/IconButton"
import {Fragment, useEffect} from "react"
import DialogContent from "@material-ui/core/DialogContent"
import DialogTitle from "@material-ui/core/DialogTitle"
import Dialog from "@material-ui/core/Dialog"
import {useConfirm} from "material-ui-confirm"
import Modal from "@material-ui/core/Modal"
import DialogActions from "@material-ui/core/DialogActions"
import Button from "@material-ui/core/Button"

export default function ShowTodos () {

  const [todos, setTodos] = React.useState<ITodo[]>([]);

  const [selectedTodo, setSelectedTodo] = React.useState<ITodo>({});

  const [dialogState, setDialogState] = React.useState<boolean>(false);

  const confirm = useConfirm();



  useEffect(() => {

      getTodos()
      .then(({data: {todos}}: ITodo[] | any) => {
        const todoList = todos
       setTodos(todoList)
      })
      .catch((err: Error) => console.log(err))


  })


  const handleConfirmation = (id: string) => {

    confirm({ description: 'This action is permanent!' })
    .then(() => { deleteTodoById(id)})
  }



  const close = () => {
    setDialogState(false)
  }
  return (
      <List>
        {todos.map((todo: ITodo ) =>
            (
              <ListItem onClick={() => {
                setSelectedTodo(todo);
                setDialogState(true)
              }
              } key={todo._id}>
                <ListItemText
                    primary={todo.text}
                />
                  <IconButton edge="end" aria-label="delete" onClick={() => handleConfirmation(todo._id)}>
                    <DeleteIcon />
                  </IconButton>

            <Dialog open={dialogState} onClose={() => setDialogState(false)} aria-labelledby="form-dialog-title">
              <DialogTitle id="form-dialog-title">Update</DialogTitle>
              <DialogContent>
                <TextField
                    defaultValue={selectedTodo.text}
                    autoFocus
                    margin="dense"
                    id="name"
                    fullWidth
                />
              </DialogContent>
              <DialogActions>
                <Button color="primary" onClick={() => setDialogState(false)}>
                  Cancel
                </Button>
                <Button  color="primary" onClick={() => {
                  updateTodo(selectedTodo);
                  setDialogState(false)

                }
                }>
                  Update
                </Button>
              </DialogActions>
            </Dialog>
              </ListItem>
            )
        )}
      </List>
  );

}
